import React, { useEffect, useState } from 'react';
import UserSelector from './components/UserSelector';
import AddUserForm from './components/AddUserForm';
import ClaimButton from './components/ClaimButton';
import Leaderboard from './components/Leaderboard';
import { fetchLeaderboard, fetchUsers } from './services/api';

function App() {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);
  const [awardedPoints, setAwardedPoints] = useState(null);

  const loadData = async () => {
    const allUsers = await fetchUsers();
    const ranks = await fetchLeaderboard();
    setUsers(allUsers);
    setLeaderboard(ranks);
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-100 to-blue-100 py-10 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-xl p-8">
        <h1 className="text-4xl font-extrabold text-center text-blue-700 mb-6">
          üèÜ User Points Leaderboard
        </h1>

        <div className="space-y-6">
          <div className="bg-slate-50 p-4 rounded-xl shadow">
            <AddUserForm onUserAdded={loadData} />
          </div>

          <div className="bg-slate-50 p-4 rounded-xl shadow">
            <UserSelector
              users={users}
              selectedUserId={selectedUserId}
              onChange={setSelectedUserId}
            />
          </div>

          <div className="bg-slate-50 p-4 rounded-xl shadow text-center">
            <ClaimButton
              selectedUserId={selectedUserId}
              onPointsClaimed={() => {
                setAwardedPoints(null);
                loadData();
              }}
            />
            {awardedPoints && (
              <p className="mt-3 text-lg text-green-600 font-semibold">
                üéâ Points Claimed: {awardedPoints}
              </p>
            )}
          </div>

          <div className="bg-slate-50 p-4 rounded-xl shadow">
            <Leaderboard leaderboard={leaderboard} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
