import React from 'react';

const Leaderboard = ({ leaderboard }) => {
  return (
    <div className="bg-gradient-to-br from-purple-100 to-white rounded-xl shadow-lg p-6">
      <h2 className="text-2xl font-bold text-purple-700 mb-4 text-center flex items-center justify-center gap-2">
        üèÜ Leaderboard
      </h2>
      <table className="w-full text-left border-collapse">
        <thead className="bg-purple-200 text-purple-800">
          <tr>
            <th className="py-3 px-4 rounded-tl-lg">Rank</th>
            <th className="py-3 px-4">Name</th>
            <th className="py-3 px-4 rounded-tr-lg">Total Points</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(leaderboard) && leaderboard.map((user, index) => (
            <tr
              key={user._id}
              className="hover:bg-purple-50 transition duration-200 ease-in-out"
            >
              <td className="py-2 px-4 font-semibold text-purple-600">{user.rank}</td>
              <td className="py-2 px-4">{user.name}</td>
              <td className="py-2 px-4">{user.totalPoints}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
